code.installcode:
$phpkd_chusrn_files = array(
	DIR . '/includes/phpkd/phpkd_chusrn_plugins.php',
	DIR . '/includes/phpkd/phpkd_chusrn_functions.php',
	DIR . '/includes/xml/cpnav_phpkd_chusrn.xml',
	DIR . '/includes/xml/bitfield_phpkd_chusrn.xml',
	DIR . '/includes/xml/hooks_phpkd_chusrn.xml',
	DIR . '/' . $vbulletin->config['Misc']['admincpdir'] . '/phpkd_chusrn.php'
);

function phpkd_chusrn_files($files)
{
	foreach ($files AS $fileid => $file)
	{
		if (file_exists($file))
		{
			unset($files[$fileid]);
		}
	}

	return $files;
}


$phpkd_chusrn_files_not_exists = phpkd_chusrn_files($phpkd_chusrn_files);

if (is_array($phpkd_chusrn_files_not_exists) AND !empty($phpkd_chusrn_files_not_exists))
{
	$phpkd_chusrn_absent_files .= "<ol>\n";
	foreach ($phpkd_chusrn_files_not_exists AS $phpkd_chusrn_absent_file)
	{
		$phpkd_chusrn_absent_files .= "\t<li>" . $phpkd_chusrn_absent_file . "</li>\n";
	}
	$phpkd_chusrn_absent_files .= "</ol>\n";
	print_dots_stop();
	print_cp_message('íÑÌì ÇáÊÃßÏ ãä ÑİÚ ãáİÇÊ ÇáãäÊÌ "äÙÇã ÊÛííÑ ãÚÑİÇÊ ÇáÃÚÖÇÁ" ßÇãáÉ ÈÔßá ÕÍíÍ ŞÈá ÊËÈíÊå!<br /><strong>ÇáãáİÇÊ ÇáäÇŞÕÉ:</strong>' . $phpkd_chusrn_absent_files);
}

