		`action_dateline` int(10) unsigned NOT NULL default '0',
		`processedby` int(10) unsigned NOT NULL default '0',
		`comment` varchar(250) NOT NULL default '',
		PRIMARY KEY  (`id`)
	) ENGINE=MyISAM
");
